/** @jsx jsx */
import { jsx } from '@emotion/core';
import { hasAfterStyles, hasBeforeStyles, linkStyles, roundedBorderStyles, textStyles } from './styles';

const Content = props => {
  const {
    elemBefore = null,
    isRemovable = true,
    text = '',
    color = 'standard',
    href,
    isRounded,
    isLink,
    linkComponent: Link = 'a',
    linkHoverColor
  } = props;

  if (isLink) {
    return jsx(Link, {
      href: href,
      "data-color": color,
      css: [linkStyles(linkHoverColor), isRounded ? roundedBorderStyles : undefined, elemBefore ? hasBeforeStyles : undefined, isRemovable ? hasAfterStyles : undefined]
    }, text);
  } else {
    return jsx("span", {
      css: [textStyles, isRounded ? roundedBorderStyles : undefined, elemBefore ? hasBeforeStyles : undefined, isRemovable ? hasAfterStyles : undefined]
    }, text);
  }
};

export default Content;